mapper.update:
UPDATE user SET failed_login_attempts = failed_login_attempts + 1,
account_locked_until = #{lockUntil},
updated_at = NOW() WHERE email = #{email}

mapper.select:
SELECT

FROM user
WHERE deleted = 0

    AND username LIKE CONCAT('%', #{condition.username}, '%')


    AND email LIKE CONCAT('%', #{condition.email}, '%')


    AND phone LIKE CONCAT('%', #{condition.phone}, '%')


    AND gender = #{condition.gender}


    AND account_status = #{condition.accountStatus}


    AND level >= #{condition.minLevel}


    AND level <= #{condition.maxLevel}


    AND country = #{condition.country}


    AND city = #{condition.city}

ORDER BY ${condition.orderBy} ${condition.orderDirection}
